pragma solidity ^0.4.18;

contract EIP20Interface {

    uint256 public totalSupply;

    function balanceOf(address _owner) public view returns (uint256 balance);
    function transfer(address _to, uint256 _value) public returns (bool success);
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success);
    function approve(address _spender, uint256 _value) public returns (bool success);
    function allowance(address _owner, address _spender) public view returns (uint256 remaining);

    event Transfer(address indexed _from, address indexed _to, uint256 _value); 
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);
}

contract DCT is EIP20Interface {

    // Token
    mapping (address => uint256) public balances;
    mapping (address => mapping (address => uint256)) public allowed;

    uint256 constant private MAX_UINT256 = 2**256 - 1;
    string public name = "Dcourt";
    uint8 public decimals = 18;
    string public symbol = "DCT";
    uint256 public totalSupply;
    address private minter;
    bool private frozen = true;

    //Functions
    function DCT() public {
        minter = msg.sender;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(!frozen && msg.sender != minter);
        require(balances[msg.sender] >= _value);
        balances[msg.sender] -= _value;
        balances[_to] += _value;
        Transfer(msg.sender, _to, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(!frozen && msg.sender != minter);
        uint256 allowance = allowed[_from][msg.sender];
        require(balances[_from] >= _value && allowance >= _value);
        balances[_to] += _value;
        balances[_from] -= _value;
        if (allowance < MAX_UINT256) {
            allowed[_from][msg.sender] -= _value;
        }
        Transfer(_from, _to, _value);
        return true;
    }

    function balanceOf(address _owner) public view returns (uint256 balance) {
        return balances[_owner];
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowed[msg.sender][_spender] = _value;
        Approval(msg.sender, _spender, _value);
        return true;
    }

    function allowance(address _owner, address _spender) public view returns (uint256 remaining) {
        return allowed[_owner][_spender];
    }

    // Only minter

    mapping (bytes32 => bool) public relayed;

    modifier onlyMinter(){
        require(msg.sender == minter);
        _;
    }

    function mint(address _to, uint256 _value) onlyMinter public returns (bool success) {
        balances[_to] += _value;
        return true;
    }

    function changeMinter(address _to) onlyMinter public returns (bool success) {
        minter = _to;
        return true;
    }

    function freeze(bool _value) onlyMinter public returns (bool success){
        frozen = _value;
        return true;
    }

    //Governance
    //function signal(address _minter) public {
        //
    //}

    //Relayed Functions
    function relayTransfer(address _from, address _to, uint256 _value, uint256 _fee, uint256 _timeout, uint8 v, bytes32 r, bytes32 s) public returns (bool success) {
        require(!frozen && msg.sender != minter);
        require(balances[_from] >= (_value + _fee) && now < _timeout);
        bytes32 hash = keccak256(_from, _to, _value, _fee, _timeout);
        require(relayed[hash] != true);
        require(ecrecover(hash, v, r, s) == _from);
        balances[_from] -= (_value + _fee);
        balances[_to] += _value;
        balances[msg.sender] += _fee;
        Transfer(_from, _to, _value);
        Transfer(_from, msg.sender, _fee);
        relayed[hash] = true;
        return true;
    }
}
